mapper.select:
select count(1)
from sys_role
where 1 = 1

	and name = #{name,jdbcType=VARCHAR}


	and short_name = #{shortName,jdbcType=VARCHAR}


	and status = #{status,jdbcType=INTEGER}

select 

from sys_role
where 1 = 1

	and name = #{name,jdbcType=VARCHAR}


	and short_name = #{shortName,jdbcType=VARCHAR}


	and status = #{status,jdbcType=INTEGER}

limit #{startRow},#{pageRows}

mapper.insert:
insert into sys_role (id, name, short_name, 
  status, creater, created_time, 
  updater, updated_time, remark
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{shortName,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{creater,jdbcType=INTEGER}, #{createdTime,jdbcType=DATE}, 
  #{updater,jdbcType=INTEGER}, #{updatedTime,jdbcType=DATE}, #{remark,jdbcType=VARCHAR}
  )
